data.response:
{
  "Basic Operation": {
    "definition": "Core switching functionality including turning lights on/off and basic operational performance, e.g. switch not working, functions well, works as expected"
  },
  "Dimming Performance": {
    "definition": "Effectiveness and range of dimming capabilities including minimum brightness levels and dimming precision, e.g. doesn't dim low enough, excellent 6% dimming, very adjustable brightness control"
  },
  "Product Longevity": {
    "definition": "How long products continue to function properly over time including premature failures and durability concerns, e.g. stopped working after 1 month, multiple switches failing within 6 months"
  },
  "Smart Connectivity": {
    "definition": "Wireless connection stability and compatibility with smart home systems and voice assistants, e.g. works with Alexa, lost Wi-Fi connection, pairing issues"
  },
  "Safety Hazards": {
    "definition": "Dangerous electrical malfunctions that pose safety risks to users and property, e.g. switch exploded, internal arcing, causing power outages"
  },
  "Thermal Management": {
    "definition": "Heat-related performance issues that affect normal operation of the switch, e.g. overheating causing automatic shutoff when dimmed to maximum"
  },
  "Response Speed": {
    "definition": "Time delays in switch activation and reaction to user commands, e.g. delay before turning on when pressing physical button"
  },
  "Advanced Features": {
    "definition": "Specialized functions beyond basic switching such as timer operations and voice control limitations, e.g. timer doesn't allow dimming, won't dim by voice command"
  },
  "Purchase Value": {
    "definition": "Customer assessment of product worth relative to price paid, e.g. worth the money, great value, good value for money"
  }
}
