{
  "Product Longevity": {
    "definition": "Customer feedback about how long products last and their long-term durability over months and years of use, e.g. breaking after 2 weeks, lasting 3+ years, failing after 2 years"
  },
  "Physical Durability": {
    "definition": "Customer concerns about the structural integrity and build quality of product components and materials, e.g. hinges breaking, toggles cracking, seeming solid or durable"
  },
  "Tactile Operation": {
    "definition": "Customer experience with the physical feel and force required when operating the product controls, e.g. smooth operation, needing to press hard, feeling nice when operating"
  },
  "Operational Sound": {
    "definition": "Customer feedback about noise levels and sound quality during product operation, e.g. silent operation, loud clicking, quiet switches, buzzing sounds"
  },
  "Visual Design": {
    "definition": "Customer perception of the product's appearance and how it integrates with their decor and aesthetic preferences, e.g. modern look, blending with decor, color differences, sophistication"
  },
  "Illumination Performance": {
    "definition": "Customer feedback about the brightness and lighting quality provided by the product's indicator lights or illumination features, e.g. very dim, excellent lighting, hard to see, full brightness"
  },
  "Installation Experience": {
    "definition": "Customer feedback about the ease or difficulty of installing and fitting the product in their electrical setup, e.g. easy installation, fits perfectly, tight in older homes, installation challenges"
  },
  "Dimming Control": {
    "definition": "Customer experience with the product's ability to adjust light brightness levels smoothly and effectively, e.g. dims great, can't dim lights, smooth dimming range, flickering issues"
  },
  "LED Compatibility": {
    "definition": "Customer feedback about how well the product works specifically with LED lighting technology, e.g. LED flickering, works with LED bulbs, LED driver compatibility"
  },
  "Wireless Connectivity": {
    "definition": "Customer experience with WiFi connection stability and network connectivity features, e.g. disconnects regularly, connects easily, WiFi setup problems, signal reliability"
  },
  "Functional Reliability": {
    "definition": "Customer feedback about consistent day-to-day operation and whether the product performs its intended functions dependably, e.g. switches dying, working flawlessly, turning on automatically, high failure rates"
  },
  "Electrical Interference": {
    "definition": "Customer concerns about the product causing unwanted electrical interactions or affecting other devices in their system, e.g. switches interfering with each other, voltage leaks, cross-activation of lights"
  },
  "Heat Management": {
    "definition": "Customer feedback about temperature control and thermal performance during operation, e.g. becoming very hot, overheating issues, handling amperage without warming"
  },
  "Mobile App Experience": {
    "definition": "Customer feedback about the quality and reliability of smartphone application features and performance, e.g. app works great, buggy app, responsive integration, scheduling issues"
  }
}
