{
  "Device Compatibility": {
    "definition": "How well the switch works with different types of lighting fixtures, bulbs, and electrical configurations, e.g. LED compatibility, 3-way setup compatibility, fixture-specific compatibility"
  },
  "System Integration": {
    "definition": "Ability to connect and work properly with smart home systems and automation platforms, e.g. HomeAssistant compatibility, Z-wave connectivity, hub pairing"
  },
  "Dimming Quality": {
    "definition": "Performance of the dimming function including smoothness, range, and lighting behavior during dimming, e.g. flickering issues, dimming range limitations, buzzing during dimming"
  },
  "Basic Operation": {
    "definition": "Fundamental on/off switching functionality and general device performance, e.g. turning lights on and off properly, working as expected, basic switching response"
  },
  "Long Term Reliability": {
    "definition": "Device durability and consistent performance over extended periods of use, e.g. failure rates over months or years, component degradation, lifespan expectations"
  },
  "Installation Experience": {
    "definition": "Ease or difficulty of physical installation and setup process, e.g. wiring complexity, fitting issues, installation time requirements"
  },
  "Thermal Management": {
    "definition": "How well the device handles heat generation and dissipation during operation, e.g. overheating issues, heat dissipation with high wattage loads"
  },
  "User Interface Responsiveness": {
    "definition": "Speed and reliability of user interactions with the device controls, e.g. touch response delays, switching delays, control sensitivity"
  },
  "Operational Noise": {
    "definition": "Sound levels produced during normal device operation and switching, e.g. clicking sounds, quiet operation, buzzing noises"
  },
  "Setup and Configuration": {
    "definition": "Initial device pairing, network setup, and configuration processes, e.g. pairing ease, setup time, configuration options"
  },
  "Customer Support Quality": {
    "definition": "Quality and responsiveness of manufacturer support services, e.g. support response times, documentation quality, technical assistance"
  },
  "Value Proposition": {
    "definition": "Cost-effectiveness and pricing considerations relative to product performance, e.g. price comparisons, value for money assessments"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are too vague, generic, or unclear to categorize meaningfully",
    "ids": [2274, 2275, 2307, 2308, 2377]
  }
}
