{
  "Flickering Issues": {
    "definition": "Problems with lights flickering, strobing, or producing unsteady illumination during operation, e.g. flickering at 50% dim, intermittent flickering, LED flicker when dimmed"
  },
  "Audible Noise": {
    "definition": "Unwanted sounds produced during operation including buzzing, squealing, or humming, e.g. buzzing when turned on, squealing sound when dimmed, buzzing at max brightness"
  },
  "Product Lifespan": {
    "definition": "How long products last before failing or needing replacement, including both premature failures and expected longevity, e.g. stopped working within 2 months, failed after several years, long-life expectation"
  },
  "Basic Functionality": {
    "definition": "Core operational capabilities including turning lights on/off, dimming control, and compatibility with different bulb types, e.g. works with LED lights, doesn't turn on lights, 3-way installation function"
  },
  "Physical Operation Feel": {
    "definition": "Tactile experience of using switches including smoothness, stiffness, and mechanical responsiveness, e.g. functions smoothly, mushy feeling actuation, sticky switch action"
  },
  "Installation Process": {
    "definition": "Ease and complexity of installing the product including wiring, fitting, and setup time, e.g. simple to wire, took 15-20 minutes to install, fits flush against wall"
  },
  "Wireless Connectivity": {
    "definition": "WiFi connection stability, setup process, and integration with smart home systems, e.g. easy WiFi connection, connects to home assistant, never breaks connection"
  },
  "Dimming Performance": {
    "definition": "Quality and smoothness of dimming functionality including transition smoothness and range control, e.g. smoothly dims, very smooth dimming transition, accessible dimmer adjustment"
  },
  "Smart Device Compatibility": {
    "definition": "Integration and performance with smart home ecosystems and voice assistants, e.g. works well with Alexa, compatible with Apple HomeKit, works with Lutron Aurora"
  },
  "App Performance": {
    "definition": "Functionality and reliability of companion mobile applications for smart switches, e.g. app works flawlessly, MyLeviton app connection automatic"
  },
  "Power Capacity": {
    "definition": "Ability to handle different electrical loads and wattage requirements without issues, e.g. works great with 1000 watts, handles 10x60W bulbs, cannot handle high wattage"
  },
  "Heat Generation": {
    "definition": "Temperature management during operation including overheating issues and thermal performance, e.g. gets very hot, started to smoke, never gets too hot with load"
  },
  "Switch Mechanism Reliability": {
    "definition": "Reliability of the physical switching mechanism including toggle and button responsiveness, e.g. on-off toggle fails frequently, switch stops catching, requires multiple presses"
  },
  "Physical Deterioration": {
    "definition": "Physical breakdown of product materials and housing over time, e.g. switches crack, fall apart during installation"
  },
  "Batch Failure Rates": {
    "definition": "Patterns of multiple product failures within purchased sets or over time periods, e.g. all fail in less than a year, 4 out of 6 failed, 10 percent failure rate"
  },
  "Electrical Safety": {
    "definition": "Safety concerns including arcing, sparking, and fire hazards during operation, e.g. arced when switching, sparked and blew out lights, started fire"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are unclear, too generic, or don't fit into meaningful performance categories",
    "ids": [3669, 3670, 3671, 3672, 3673, 3674, 3678, 3682, 3683, 3726, 3729, 3731]
  }
}
