{
  "Installation Ease": {
    "definition": "How simple and straightforward the product is to install, including time required and complexity of setup, e.g. quick installation, easy wiring, simple setup process"
  },
  "Physical Fit": {
    "definition": "How well the product physically fits in the intended space or with accessories like faceplates and boxes, e.g. flush mounting, faceplate compatibility, box clearance"
  },
  "Electrical Compatibility": {
    "definition": "How well the product works with different electrical configurations, lighting types, and other electrical components, e.g. LED compatibility, 3-way circuits, voltage requirements"
  },
  "Dimming Performance": {
    "definition": "The quality and effectiveness of dimming functionality including range and smoothness, e.g. smooth dimming, full range control, dimming accuracy"
  },
  "Light Flickering Control": {
    "definition": "The product's ability to prevent or control light flickering issues, particularly with LED lights, e.g. flicker elimination, stable light output, consistent illumination"
  },
  "Switch Responsiveness": {
    "definition": "The tactile feel and mechanical response of the switch operation including click quality and smoothness, e.g. positive click, smooth action, definitive feedback"
  },
  "Operational Noise": {
    "definition": "Sound levels produced during normal operation including buzzing, humming, or clicking sounds, e.g. quiet operation, buzzing issues, audible feedback"
  },
  "Product Durability": {
    "definition": "How well the product withstands regular use over time including mechanical failure rates, e.g. long-term reliability, component failure, wear resistance"
  },
  "Manufacturing Quality": {
    "definition": "The consistency and quality of manufacturing including defect rates and build quality, e.g. quality control issues, defective units, construction quality"
  },
  "Basic Functionality": {
    "definition": "Core operational performance including on/off switching and general working condition, e.g. reliable switching, proper operation, functional performance"
  },
  "Electrical Safety": {
    "definition": "Safety aspects related to electrical installation and operation including wire exposure and hazard prevention, e.g. safe installation features, fire safety, electrical protection"
  },
  "Response Timing": {
    "definition": "Speed of response when operating the switch including any delays in activation, e.g. instant response, activation delay, switching speed"
  },
  "Wire Connection": {
    "definition": "Quality and reliability of electrical wire connections and termination methods, e.g. connection security, wire handling, termination reliability"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are too vague, generic, or unclear to categorize meaningfully",
    "ids": [2810, 2811, 2812, 2813]
  }
}
