{
  "Smart Home Integration": {
    "definition": "Compatibility and performance with smart home platforms and voice assistants, e.g. HomeKit compatibility, Alexa integration, Google Assistant connectivity"
  },
  "Product Longevity": {
    "definition": "How long the product continues to function reliably over time, e.g. failing after months, lasting years, long-term durability"
  },
  "Operational Reliability": {
    "definition": "Consistency of product performance during regular use, e.g. intermittent failures, unresponsiveness, signal reliability"
  },
  "Core Switch Operation": {
    "definition": "Basic on/off switching functionality and mechanical operation, e.g. manual control, switch toggling, physical button response"
  },
  "Advanced Features": {
    "definition": "Specialized functionality beyond basic switching, e.g. auto-off timers, delayed shutoff, automation capabilities"
  },
  "Dimming Performance": {
    "definition": "Quality and effectiveness of light dimming capabilities, e.g. LED compatibility, dimming range, flicker prevention"
  },
  "Installation Experience": {
    "definition": "Ease and complexity of product installation and setup, e.g. wiring difficulty, setup time, installation requirements"
  },
  "Physical Durability": {
    "definition": "Structural integrity and resistance to physical wear, e.g. plastic cracking, component breakage, physical robustness"
  },
  "Wireless Connectivity": {
    "definition": "Quality of wireless network connections and communication, e.g. WiFi connectivity, network range, connection stability"
  },
  "Tactile Operation": {
    "definition": "Physical feel and responsiveness of switch operation, e.g. button pressure, mechanical feedback, operation smoothness"
  },
  "Audio Feedback": {
    "definition": "Sound characteristics during switch operation, e.g. clicking noise, operational quietness, audible feedback"
  },
  "Hardware Components": {
    "definition": "Quality and performance of physical hardware elements, e.g. screw quality, mounting components, hardware integrity"
  },
  "Electrical Capacity": {
    "definition": "Power handling capabilities and electrical performance limits, e.g. wattage ratings, motor compatibility, load capacity"
  },
  "Mobile App Experience": {
    "definition": "Quality and usability of companion mobile applications, e.g. app functionality, software updates, user interface"
  },
  "Built-in Lighting": {
    "definition": "Performance of integrated LED indicators or night lights, e.g. illumination quality, light sensitivity, indicator brightness"
  },
  "Safety Concerns": {
    "definition": "Product safety issues and potential hazards, e.g. fire risks, electrical safety, hazardous malfunctions"
  },
  "Memory Functions": {
    "definition": "Ability to remember and restore previous settings, e.g. dimming level memory, setting retention"
  },
  "Device Compatibility": {
    "definition": "Performance with specific bulbs, fixtures, and electrical devices, e.g. LED bulb compatibility, fixture-specific performance"
