data.response:
{
  "Product Longevity": {
    "definition": "Aspects related to how long products last and their ability to withstand extended use over time, e.g. switches failing after months/years, breaking down quickly, lasting a long time"
  },
  "Operational Reliability": {
    "definition": "Aspects related to consistent and dependable day-to-day functioning without unexpected failures or erratic behavior, e.g. switches working reliably, turning on by themselves, stopping response, being dependable"
  },
  "Switch Operation Feel": {
    "definition": "Aspects related to the tactile experience and mechanical smoothness of operating the physical switch, e.g. smooth switching, positive click, sliding feel, crisp operation"
  },
  "Dimming Performance": {
    "definition": "Aspects related to how well the dimming function works including range and compatibility with different bulb types, e.g. dimming range, working with LEDs, not dimming low enough, excellent dimming function"
  },
  "Light Flickering Issues": {
    "definition": "Aspects related to unwanted light flickering, flashing, or strobing behavior, e.g. lights flickering, flashing like strobe, flickering with LEDs, flickering at all levels"
  },
  "Audible Noise Problems": {
    "definition": "Aspects related to unwanted sounds produced by the switch during operation, e.g. buzzing sounds, weird noises, humming, sound like flies"
  },
  "Installation Experience": {
    "definition": "Aspects related to the ease or difficulty of physically installing the switch including wiring and setup, e.g. easy installation, hard to install, breaking parts during install, fitting in junction box"
  },
  "Smart Connectivity": {
    "definition": "Aspects related to WiFi connection stability and smart home integration capabilities, e.g. connecting to WiFi, pairing with Alexa, HomeKit integration, losing connection"
  },
  "Three Way Switch Function": {
    "definition": "Aspects specifically related to three-way switch operation and performance, e.g. 3-way switching working, requiring multiple toggles, wiring complications for 3-way"
  },
  "Smart Features Responsiveness": {
    "definition": "Aspects related to how well smart features like app control, voice commands, and scheduling respond and function, e.g. responsive to app, voice command issues, scheduling problems"
  },
  "Manufacturing Quality": {
    "definition": "Aspects related to build quality and defect rates in manufacturing, e.g. defective out of box, terminals breaking, screws breaking, multiple units being faulty"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are too generic, vague, or don't provide specific actionable feedback about product performance",
    "ids": [2451, 2479, 2481, 2541]
  }
}
