{
  "Physical Size and Fit": {
    "definition": "Overall dimensions, depth, and space requirements affecting installation compatibility, e.g. shallow profile, oversized electronics box, fits in small switch boxes"
  },
  "Visual Design and Aesthetics": {
    "definition": "Overall appearance, style, and visual appeal of the switch face and design, e.g. sleek appearance, modern look, toggle style, rocker design"
  },
  "Color and Finish Quality": {
    "definition": "Color accuracy, finish consistency, and coating quality affecting visual matching, e.g. off-white that looks dirty, brass finish like spray paint, color matching issues"
  },
  "Control Interface Design": {
    "definition": "Physical design and ergonomics of user interaction elements like buttons, paddles, and sliders, e.g. paddle button design, thick switch levers, button illumination"
  },
  "Tactile Operation Feel": {
    "definition": "Physical sensation and feedback during switch operation and control adjustment, e.g. smooth mechanism, satisfying click, slider movement smoothness"
  },
  "Dimmer Control Mechanics": {
    "definition": "Physical design and operation of dimming adjustment mechanisms and controls, e.g. slider catches, sharp edges on dimmer, adjustment dial functionality"
  },
  "Structural Build Quality": {
    "definition": "Overall construction robustness, material quality, and structural integrity, e.g. solid build construction, heavy duty construction, cheap plastic construction"
  },
  "Wiring and Connection Hardware": {
    "definition": "Physical wire connection methods, terminals, and electrical connection components, e.g. screw terminals, push-in connectors, wire nuts included"
  },
  "Indicator Light Performance": {
    "definition": "Visibility, brightness, and functionality of LED indicators and status lights, e.g. dim indicator light, inconsistent brightness, blue LED indicator"
  },
  "Component Completeness": {
    "definition": "Inclusion or absence of necessary parts, accessories, and mounting hardware, e.g. missing faceplate, screws included, wire nuts provided"
  },
  "Product Condition Issues": {
    "definition": "Physical damage, defects, or condition problems affecting product integrity, e.g. damaged packaging, burnt wires, crooked alignment"
  },
  "Specialized Features": {
    "definition": "Unique physical features and specialized functionality beyond basic switching, e.g. night light border, flicker control wheel, minimum brightness dial"
  }
}
