{
  "Room Specific Lighting": {
    "definition": "Lighting control for specific rooms and spaces throughout the home, e.g. bedroom lighting, bathroom lighting, hallway lighting, kitchen lighting, dining room lighting, closet installation"
  },
  "Ceiling Fan Control": {
    "definition": "Control and dimming of ceiling fans and their integrated lighting systems, e.g. bedroom fan control, bathroom fan and light control, Hunter ceiling fan control, multiple fans in home"
  },
  "LED Light Management": {
    "definition": "Control and dimming of various LED lighting applications and installations, e.g. LED strip dimming, under cabinet LED lighting, recessed LED lights, LED ceiling light control"
  },
  "Ambiance and Scene Control": {
    "definition": "Creating specific lighting moods and cycling through different lighting scenes, e.g. romantic lighting, room ambiance control, scene cycling, nighttime use"
  },
  "Smart Home Integration": {
    "definition": "Integration with home automation systems and voice control platforms, e.g. home automation with Alexa, smart home integration, remote control from another room via app"
  },
  "Switch Replacement and Upgrades": {
    "definition": "Replacing existing switches with newer models or different switch types, e.g. replacing old toggle switches, replacement for rotary dimmer, switching from toggle to paddle switch"
  },
  "Renovation and Construction": {
    "definition": "Installation during home improvement projects and new construction, e.g. house remodeling, renovation projects, house flipping, modular home installation"
  },
  "Specialty and Custom Applications": {
    "definition": "Unique or specialized lighting control applications beyond standard home lighting, e.g. fuel gauge dimming, auto stereo control, camper van LED lights, makeup mirror lighting"
  },
  "Property Management": {
    "definition": "Lighting solutions for rental properties and guest accommodations, e.g. rental property lighting, guest control, guest room fan control"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are too generic or unclear to categorize meaningfully",
    "ids": [27, 51, 73, 74, 78]
  }
}
