{
  "Operational Reliability": {
    "definition": "Consistency and dependability of basic switch operation over time, including intermittent failures, random behavior, and overall functional stability, e.g. works intermittently, turns on/off randomly, stops working after months"
  },
  "Product Longevity": {
    "definition": "How long the product continues to function before failure or degradation, including premature failures and expected lifespan, e.g. dead on arrival, stops working after 8 months, lasts years without issues"
  },
  "Physical Durability": {
    "definition": "Resistance to physical wear, damage, and mechanical stress during normal use, e.g. extremely fragile, breaks under pressure, stops sliding"
  },
  "Dimming Performance": {
    "definition": "Quality and range of light dimming functionality, including smoothness, range limitations, and flickering issues, e.g. limited dimming range, flickers at middle dimness, smooth dimming to very low levels"
  },
  "LED Compatibility": {
    "definition": "How well the switch works specifically with LED bulbs, including interference, flickering, and dimming performance, e.g. doesn't work well with LED bulbs, no flickering with LEDs, buzzing with LED bulbs"
  },
  "WiFi Connectivity": {
    "definition": "Stability and reliability of wireless internet connection, including setup ease and connection maintenance, e.g. loses WiFi connection frequently, seamless WiFi connectivity, won't connect to 5G"
  },
  "Smart Home Integration": {
    "definition": "Compatibility and performance with voice assistants and home automation platforms, e.g. works flawlessly with Alexa, buggy with Home Assistant, integrates seamlessly with automation systems"
  },
  "Device Pairing": {
    "definition": "Ease and reliability of initial device connection and setup with hubs or networks, e.g. pairing can be difficult, easy to pair, had to hold next to coordinator"
  },
  "Response Speed": {
    "definition": "How quickly the switch responds to commands from physical controls, apps, or voice commands, e.g. lights respond quickly, dimming slow to respond, app control was instant"
  },
  "Installation Complexity": {
    "definition": "Ease of physical installation and wiring process, e.g. extremely easy to wire, more complicated than expected, installed in 5 minutes"
  },
  "Setup Instructions": {
    "definition": "Quality and clarity of provided setup documentation and guidance, e.g. instructions are inaccurate, instructions were very clear, lacking detail"
  },
  "Signal Range": {
    "definition": "Effective operating distance and signal strength between remote controls and receivers, e.g. poor range at 4-12 feet, great range when turning on, connection issues when wall mounted"
  },
  "Switch Position Clarity": {
    "definition": "Ability to easily determine the current on/off state of the switch through visual or tactile feedback, e.g. difficult to realize switch is on or off"
  }
}
