data.response:
{
  "Installation Ease": {
    "definition": "Customer feedback about the difficulty or simplicity of physically installing the switch, including required skills and time, e.g. easy installation, requires basic handyman skills, straightforward replacement"
  },
  "Physical Operation Feel": {
    "definition": "Customer experience with the tactile feedback and force required to operate the switch mechanism, e.g. hard to press, sticky buttons, smooth operation, requires significant force"
  },
  "Operational Noise": {
    "definition": "Sound levels produced during normal switch operation and activation, e.g. loud clicking sounds, quiet operation, inconsistent activation sounds"
  },
  "Build Quality Perception": {
    "definition": "Customer assessment of the physical construction and materials quality of the switch, e.g. feels cheap, flimsy construction, mushy feeling, poor quality materials"
  },
  "Product Longevity": {
    "definition": "How long the switch continues to function before failure or degradation, e.g. fails within months, stops working after a year, long-term reliability"
  },
  "Electrical Performance": {
    "definition": "Core electrical functionality including power delivery and basic switching operations, e.g. fails to energize load, powers on but doesn't control lights, voltage issues"
  },
  "Dimming Control": {
    "definition": "Performance of dimming functionality including range and smoothness of light level adjustments, e.g. dims linearly, only dims to 25%, works in steps rather than smooth transitions"
  },
  "Device Compatibility": {
    "definition": "How well the switch works with different types of bulbs, fixtures, and wiring configurations, e.g. LED compatibility, works with specific bulb types, wiring layout compatibility"
  },
  "Smart Features Setup": {
    "definition": "Ease of configuring smart functionality and connecting to apps or networks, e.g. quick app setup, easy Alexa integration, WiFi connection difficulties"
  },
  "Smart Home Integration": {
    "definition": "Performance with voice assistants and smart home ecosystems after initial setup, e.g. works with Google Home, Alexa compatibility, ecosystem integration"
  },
  "Wireless Connectivity": {
    "definition": "Reliability of WiFi connection and network communication for smart switches, e.g. maintains network connectivity, pairing failures, connection drops over time"
  },
  "App Control Performance": {
    "definition": "Functionality of smartphone app control and remote operation capabilities, e.g. app commands work properly, remote control features, grouping abilities"
  },
  "Advanced Smart Features": {
    "definition": "Specialized smart functionality beyond basic on/off control, e.g. fade rate controls, double tap actions, scheduling features, soft on/off"
  },
  "Visual Appeal": {
    "definition": "Customer satisfaction with the aesthetic appearance and design of the switch, e.g. looks great, attractive design, visual integration with decor"
  },
  "Response Timing": {
    "definition": "Speed of switch response from activation to light control, e.g. slight delay between pressing and light activation, immediate response"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are too generic, unclear, or don't represent specific performance characteristics",
    "ids": [3815, 3816, 3817, 3856, 3857, 3909, 3917]
  }
}
