{
  "WiFi Connectivity": {
    "definition": "Ability to establish and maintain stable wireless network connections, including initial setup, connection stability, and network compatibility, e.g. connecting to mesh networks, maintaining connection over time, reconnecting after outages"
  },
  "Mobile App Experience": {
    "definition": "Quality and usability of the companion mobile application for device control and configuration, e.g. ease of navigation, scheduling features, control responsiveness, interface design"
  },
  "Smart Home Integration": {
    "definition": "Compatibility and seamless operation with voice assistants and smart home ecosystems, e.g. Alexa integration, Google Home connectivity, HomeKit compatibility"
  },
  "Device Reliability": {
    "definition": "Consistent operational performance and freedom from unexpected failures over time, e.g. working without issues for years, maintaining functionality during weather events, avoiding random disconnections"
  },
  "Product Defects": {
    "definition": "Manufacturing flaws and out-of-box failures that prevent normal operation, e.g. dead on arrival units, electrical shorts, complete non-functionality from purchase"
  },
  "Dimming Performance": {
    "definition": "Quality and precision of light brightness control functionality, e.g. smooth dimming range, LED compatibility, absence of flickering, precise brightness adjustment"
  },
  "Installation Process": {
    "definition": "Ease and complexity of physical device installation and wiring, e.g. fitting in junction boxes, wire connection difficulty, compatibility with existing electrical configurations"
  },
  "Physical Operation": {
    "definition": "Tactile feel and mechanical responsiveness of physical controls and switches, e.g. switch throw quality, button responsiveness, operational smoothness, noise levels"
  },
  "Initial Setup": {
    "definition": "First-time device configuration and pairing process with networks and systems, e.g. hub connection speed, app setup difficulty, device pairing complexity"
  },
  "Device Durability": {
    "definition": "Long-term physical and operational lifespan under normal usage conditions, e.g. years of reliable service, resistance to wear, expected brand longevity"
  },
  "Response Time": {
    "definition": "Speed of device reaction to commands and inputs from various control methods, e.g. voice command responsiveness, app control lag, instant activation"
  },
  "Customization Options": {
    "definition": "Availability and ease of personalizing device settings and appearance, e.g. programmable features, interface lighting options, button configuration flexibility"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are too vague, unclear, or don't represent meaningful performance categories",
    "ids": [3307, 3308, 3309, 3310, 3311]
  }
}
