{
  "Build Quality": {
    "definition": "Manufacturing quality and construction standards affecting product integrity, e.g. poor quality control, soldering defects, overall construction standards"
  },
  "Basic Functionality": {
    "definition": "Core operational performance and whether the device performs its primary switching function, e.g. works as expected, doesn't work at all, meets specifications"
  },
  "User Interface Design": {
    "definition": "Ease of use and intuitiveness of physical controls and interface elements, e.g. clear interface, user-friendly design, easy navigation"
  },
  "Dimming Performance": {
    "definition": "Quality and characteristics of light dimming capabilities including range and smoothness, e.g. smooth dimming, dimming range, flickering issues, adjustment curves"
  },
  "Physical Switch Operation": {
    "definition": "Mechanical feel and operation of physical switch mechanisms and controls, e.g. switch action smoothness, pressure requirements, tactile feedback"
  },
  "Device Reliability": {
    "definition": "Long-term dependability and consistent operation over time, e.g. years of reliable service, premature failure, consistent performance"
  },
  "Safety Performance": {
    "definition": "Fire hazards, overheating, and electrical safety concerns during operation, e.g. overheating issues, fire hazards, electrical sparking"
  },
  "Installation Process": {
    "definition": "Ease and complexity of physical installation and setup procedures, e.g. straightforward installation, wiring complexity, physical fitting challenges"
  },
  "Smart Connectivity": {
    "definition": "Wireless connection stability and setup for smart home integration, e.g. WiFi reliability, Bluetooth pairing, network connectivity issues"
  },
  "Smart Features": {
    "definition": "Advanced digital capabilities and smart home platform integration, e.g. app control, voice commands, platform compatibility, energy monitoring"
  },
  "Device Compatibility": {
    "definition": "Ability to work with different bulb types, electrical loads, and system configurations, e.g. LED compatibility, load capacity, multi-way circuits"
  },
  "Electrical Performance": {
    "definition": "Electrical characteristics and power delivery quality affecting connected devices, e.g. voltage stability, electrical noise, power regulation"
  },
  "Response Time": {
    "definition": "Speed of device reaction from user input to light activation or adjustment, e.g. switching delays, dimming response speed, lag time"
  },
  "Visual Indicators": {
    "definition": "Visibility features and status indication capabilities of the device, e.g. nightlight function, status LEDs, visibility in dark conditions"
  },
  "Light Output Quality": {
    "definition": "Characteristics of illumination provided by built-in lighting features, e.g. brightness levels, light distribution, illumination adequacy"
  },
  "Product Durability": {
    "definition": "Long-term physical and functional integrity under normal use conditions, e.g. component longevity, wear resistance, degradation over time"
  }
}
