{
  "Visual Design": {
    "definition": "Aesthetic appearance, styling, and visual appeal of switches including modern looks, clean design, and decorative elements, e.g. appealing appearance, modern look with wall plate, clean simple design"
  },
  "Material Quality": {
    "definition": "Physical materials used in construction and their perceived quality including plastic quality and material durability, e.g. cheap weak materials, quality materials, nice plastic quality"
  },
  "Build Construction": {
    "definition": "Overall structural integrity, manufacturing quality, and how well components are assembled together, e.g. solid feel and construction, well built feel, feels cheap despite high price"
  },
  "Physical Durability": {
    "definition": "Long-term structural reliability and resistance to breaking, cracking, or component failure during use, e.g. frame breaks in middle, paddle pops out, switch fell apart during installation"
  },
  "Size and Fit": {
    "definition": "Physical dimensions and compatibility with standard electrical boxes and wall plates, e.g. fits standard switch box, larger than standard control unit, deep dimensions requiring sufficient space"
  },
  "Switch Operation": {
    "definition": "Physical feel and mechanics of the switching action including click sounds, smoothness, and tactile feedback, e.g. definitive click sound, switches feel hard and cheap, crisp click when switching"
  },
  "Wire Connection System": {
    "definition": "Physical wiring terminals, connection methods, and ease of making electrical connections, e.g. wire plug-in terminals, push-in holes don't work, sharp plastic edges on connections"
  },
  "Control Interface": {
    "definition": "Physical buttons, knobs, dials, and adjustment mechanisms used to operate the device, e.g. intuitive user-friendly controls, button breaks off, spinning ring around center button"
  },
  "Indicator Lights": {
    "definition": "Built-in LED lights, night lights, and visual status indicators on the device, e.g. integrated orange night light, red light in center, LED indicator should flash but doesn't"
  },
  "Mounting and Installation": {
    "definition": "Physical attachment methods, mounting hardware, and how the device secures to walls or existing switches, e.g. doesn't stay attached well, easy to pull off wall, clamps over light switch"
  },
  "Packaging Quality": {
    "definition": "Physical condition of packaging, included components, and protection during shipping, e.g. damaged packaging with parts outside, missing screws, switches arrived broken"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are too vague, generic, or unclear to categorize meaningfully",
    "ids": [2790, 2796, 2797, 2798, 2805, 2812, 2818, 2819, 2847, 2856, 2857, 2858]
  }
