{
  "Network Connectivity": {
    "definition": "Aspects related to establishing and maintaining wireless network connections, including WiFi setup, signal strength, and connection stability, e.g. WiFi disconnections, network setup issues, signal detection problems"
  },
  "Smart Home Integration": {
    "definition": "Aspects concerning compatibility and functionality with smart home platforms, voice assistants, and automation systems, e.g. Alexa compatibility, Z-Wave pairing, SmartThings integration, Matter support"
  },
  "Physical Installation": {
    "definition": "Aspects related to the physical mounting, wiring, and setup process of the switch hardware, e.g. wiring difficulties, space requirements in switch boxes, installation complexity"
  },
  "Product Longevity": {
    "definition": "Aspects concerning the long-term durability and lifespan of the switch under normal use conditions, e.g. switches failing after years of use, material degradation, component breakage"
  },
  "Dimming Performance": {
    "definition": "Aspects specifically related to the dimming functionality and brightness control capabilities, e.g. dimming range limitations, smooth dimming operation, brightness adjustment issues"
  },
  "Electrical Compatibility": {
    "definition": "Aspects concerning compatibility with different electrical configurations, circuits, and load types, e.g. 3-way circuit compatibility, GFCI circuit issues, load handling capacity"
  },
  "Operational Reliability": {
    "definition": "Aspects related to consistent day-to-day functionality and performance reliability, e.g. switches working as expected, intermittent failures, out-of-box functionality"
  },
  "System Responsiveness": {
    "definition": "Aspects concerning the speed and reliability of switch response to commands from various control methods, e.g. paddle press response, Z-Wave command response, lag issues"
  },
  "Status Accuracy": {
    "definition": "Aspects related to the switch correctly reporting and maintaining its current state across different control interfaces, e.g. app status mismatches, manual switch detection, state synchronization"
  },
  "Light Quality Issues": {
    "definition": "Aspects concerning unwanted visual or auditory effects during switch operation, e.g. LED flickering, electrical buzzing, humming noises"
  },
  "User Interface Visibility": {
    "definition": "Aspects related to the visibility and illumination of switch indicators and controls in various lighting conditions, e.g. switch visibility in dark rooms, indicator brightness levels"
  },
  "Physical Operation Feel": {
    "definition": "Aspects concerning the tactile experience and mechanical feel of physically operating the switch, e.g. switch firmness, smooth action, clicking sounds"
  },
  "Visual Design Integration": {
    "definition": "Aspects related to how well the switch appearance integrates with existing home decor and other switches, e.g. color matching, aesthetic blending, visual consistency"
  },
  "Firmware Management": {
    "definition": "Aspects concerning software updates and firmware-related functionality improvements or issues, e.g. firmware update processes, stability improvements, update reliability"
  },
  "Memory and Configuration": {
    "definition": "Aspects related to the switch remembering settings and maintaining user preferences across power cycles, e.g. brightness level memory, configuration retention, parameter settings"
  },
  "Manufacturing Quality": {
    "definition": "Aspects concerning the overall build quality and consistency of the manufactured product, e.g. defective units, quality control issues, construction durability"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are unclear, too generic, or don't fit into meaningful performance categories",
    "ids": [4089, 4125, 4139, 4140, 4168, 4209, 4214, 4215, 4218, 4219]
  }
}
