{
  "Dimming Performance": {
    "definition": "Quality and smoothness of dimming functionality, including range control and flicker-free operation, e.g. smooth dimming, no flicker at low levels, proper brightness range"
  },
  "Electrical Stability": {
    "definition": "Consistent electrical operation without unwanted effects like buzzing, flashing, or flickering during normal use, e.g. no buzzing sounds, no light flashing, stable power delivery"
  },
  "Heat Generation": {
    "definition": "Amount of heat produced during operation and thermal management effectiveness, e.g. running cool, excessive heating, normal warmth levels"
  },
  "Load Compatibility": {
    "definition": "Ability to work properly with different types and capacities of lighting loads, e.g. LED compatibility, high wattage handling, fluorescent support"
  },
  "Response Timing": {
    "definition": "Speed and timing characteristics of switching and dimming operations, e.g. startup delays, instant response, timing lag issues"
  },
  "Product Longevity": {
    "definition": "How long the product continues to function properly before failure or degradation, e.g. lasting years without issues, failing after months, gradual performance decline"
  },
  "Installation Process": {
    "definition": "Ease and complexity of physical installation including instructions and hardware adequacy, e.g. quick setup, clear instructions, proper wire connections"
  },
  "Wireless Connectivity": {
    "definition": "Ability to establish and maintain stable Wi-Fi connections and network communication, e.g. reliable Wi-Fi connection, connection drops, network setup issues"
  },
  "Touch Responsiveness": {
    "definition": "Physical button and touch interface response quality and reliability, e.g. responsive touch controls, unresponsive buttons, erratic operation"
  },
  "Smart Home Integration": {
    "definition": "Compatibility and functionality with voice assistants and smart home platforms, e.g. Alexa integration, Google Home compatibility, smart platform connectivity"
  },
  "Mobile App Experience": {
    "definition": "Quality and usability of companion mobile applications for device control and setup, e.g. easy app setup, app functionality issues, user interface quality"
  },
  "Advanced Features": {
    "definition": "Specialized functionality beyond basic switching including timers, scheduling, and memory functions, e.g. timer functions, brightness memory, scheduling capabilities"
  },
  "Overall Reliability": {
    "definition": "General dependability and consistent performance across all basic functions over time, e.g. works flawlessly, consistent operation, general functionality"
  }
}
