{
  "Smart Home Integration": {
    "definition": "Aspects related to connectivity with smart home ecosystems, voice assistants, and wireless network stability, e.g. Alexa compatibility, WiFi connection issues, app performance"
  },
  "Device Reliability": {
    "definition": "Aspects concerning consistent operation and failure rates over time, including switches that work intermittently or stop functioning, e.g. switches failing after short periods, inconsistent performance, dead on arrival units"
  },
  "Product Longevity": {
    "definition": "Aspects related to how long products last under normal use conditions and their resistance to wear, e.g. breaking after one month, lasting for years, withstanding heavy use"
  },
  "Light Control Performance": {
    "definition": "Aspects concerning how well the device controls lighting including dimming accuracy, response time, and unwanted light behavior, e.g. random turning on/off, slow response times, precise dimming control"
  },
  "LED Compatibility": {
    "definition": "Aspects specifically related to how well the device works with LED lighting technology, e.g. LED flickering issues, compatibility with LED applications, eliminating LED buzzing"
  },
  "Installation Compatibility": {
    "definition": "Aspects related to physical fit and installation requirements in electrical boxes and configurations, e.g. fitting in small junction boxes, multi-gang configurations, wire connection challenges"
  },
  "Electrical Performance": {
    "definition": "Aspects concerning electrical behavior including voltage handling, current management, and power-related issues, e.g. voltage bleed when off, current bleeding causing glow, power rating capabilities"
  },
  "Noise Generation": {
    "definition": "Aspects related to unwanted sounds produced during operation, e.g. buzzing noise, high pitched electrical hum, clicking sounds"
  },
  "Visual Indicators": {
    "definition": "Aspects concerning built-in lighting features for visibility and wayfinding, e.g. locator light functionality, visibility in dark conditions, illumination performance"
  },
  "Physical Build Quality": {
    "definition": "Aspects related to the tactile feel, construction quality, and physical robustness of the device, e.g. cheap feeling operation, flimsy feel on wall, solid construction"
  },
  "Device Compatibility": {
    "definition": "Aspects concerning compatibility with different platforms, devices, and electrical configurations, e.g. Android compatibility, 3-way switch compatibility, mixed lighting applications"
  },
  "Aesthetic Integration": {
    "definition": "Aspects related to visual appearance and how well the device matches existing decor and fixtures, e.g. color matching with outlet covers, modern appearance, fingerprint resistance"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are too vague, generic, or unclear to categorize meaningfully",
