{
  "Light Flickering": {
    "definition": "Issues with lights producing unwanted intermittent or strobing illumination when controlled by the switch, e.g. strobe-like effects, periodic flashing, unstable light output during dimming"
  },
  "Dimming Performance": {
    "definition": "Quality and smoothness of the dimming control functionality including range and adjustment precision, e.g. smooth stepless dimming, chunky dimming transitions, insufficient dimming range"
  },
  "Product Longevity": {
    "definition": "How long the switch continues to function properly before failure or degradation, e.g. failing after 2-3 months, lasting extended periods, breaking within first year"
  },
  "Operational Consistency": {
    "definition": "Reliability of the switch to perform its intended function when activated, e.g. working intermittently, consistent operation every time, high failure rates"
  },
  "Installation Ease": {
    "definition": "Simplicity and straightforwardness of the physical installation process, e.g. simple 10-minute replacement, complex wiring requirements, mounting difficulties"
  },
  "Device Compatibility": {
    "definition": "How well the switch works with different types of bulbs and lighting systems, e.g. LED compatibility, incandescent bulb support, wire gauge limitations"
  },
  "Smart Connectivity": {
    "definition": "Performance of wireless connection and integration with smart home systems, e.g. WiFi stability, Alexa pairing issues, network setup difficulties"
  },
  "Response Speed": {
    "definition": "Time delay between switch activation and light response, e.g. 1-2 second lag, immediate responsiveness, slow manual switch response"
  },
  "Multi Way Operation": {
    "definition": "Functionality when used in 3-way switch configurations with multiple control points, e.g. compatibility with dumb switches, proper 3-way behavior"
  },
  "Audible Noise": {
    "definition": "Unwanted sounds produced during switch operation or while in use, e.g. clicking noises, buzzing during dimming, loud switching sounds"
  },
  "Power Capacity": {
    "definition": "Ability to handle electrical loads without failure or tripping breakers, e.g. load handling limits, insufficient capacity for multiple bulbs"
  },
  "Settings Memory": {
    "definition": "Ability to remember and restore previous dimming levels and configurations, e.g. returning to last dimming setting after power cycle"
  },
  "Physical Operation": {
    "definition": "Tactile experience and force requirements for manual switch operation, e.g. excessive pressure needed, satisfying click feel, switch mechanism quality"
  },
  "Sensor Functionality": {
    "definition": "Performance of light sensors and automatic activation features, e.g. ambient light detection, motion sensing accuracy, nightlight operation"
  },
  "Manufacturing Quality": {
    "definition": "Build quality and absence of defects in the product, e.g. defective units, flimsy construction, rigorous safety testing"
  },
  "Physical Fit": {
    "definition": "How well the switch fits in electrical boxes and mounting configurations, e.g. multi-gang box compatibility, proper fitment in standard boxes"
  }
}
