{
  "Smart Home Integration": {
    "definition": "Aspects related to connecting and syncing switches with smart home systems, apps, and wireless protocols, e.g. Philips Hue app connectivity, HomeKit pairing, Zigbee bridge compatibility"
  },
  "Operational Reliability": {
    "definition": "Aspects concerning consistent day-to-day performance and dependability of switch operation, e.g. always working when activated, consistent response, no operational problems"
  },
  "Dimming Performance": {
    "definition": "Aspects specifically about the quality and effectiveness of brightness adjustment capabilities, e.g. smooth dimming operation, precise brightness control, full dimming range"
  },
  "Customization Capabilities": {
    "definition": "Aspects related to personalizing switch functions, scenes, and automation features, e.g. button programming, scene creation, automation setup"
  },
  "Installation Process": {
    "definition": "Aspects concerning the ease and complexity of physical switch installation and wiring, e.g. easy installation, wiring diagram clarity, neutral wire requirements"
  },
  "Build Quality": {
    "definition": "Aspects related to the physical construction quality and materials of the switch, e.g. well-built construction, high quality materials, sturdy design"
  },
  "Physical Durability": {
    "definition": "Aspects concerning the switch's ability to withstand wear and maintain structural integrity over time, e.g. paddle cracking, tab breaking, physical robustness"
  },
  "Product Longevity": {
    "definition": "Aspects related to how long the switch continues to function before failure or degradation, e.g. short lifespan, working for years, early failure patterns"
  },
  "Bulb Compatibility": {
    "definition": "Aspects concerning how well switches work with different types of lighting, e.g. LED bulb compatibility, multi-colored LED support, flickering prevention"
  },
  "Visual Appearance": {
    "definition": "Aspects related to the aesthetic design and visual integration of switches, e.g. matching wall plates, looking identical to brand-name switches, visual appeal"
  },
  "Light Flickering": {
    "definition": "Aspects specifically about unwanted light flickering behavior and stability, e.g. flickering on low settings, constant flickering issues, flicker-free operation"
  },
  "Noise Generation": {
    "definition": "Aspects concerning audible sounds produced by switches during operation, e.g. buzzing noise, humming sounds, quiet operation"
  },
  "Response Speed": {
    "definition": "Aspects related to how quickly switches respond to user input and activate lighting, e.g. activation delay, immediate response, lag time"
  },
  "Fan Control": {
    "definition": "Aspects specifically about controlling ceiling fan speed and operation, e.g. fan speed adjustment, fan motor control, fan-specific functionality"
  },
  "Feature Richness": {
    "definition": "Aspects concerning additional capabilities and advanced functions beyond basic switching, e.g. remembering settings, timed shutoff, brightness range"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are too vague, generic, or unclear to categorize meaningfully",
    "ids": [21, 22, 126]
  }
}
