{
  "Smart Home Integration": {
    "definition": "Performance aspects related to compatibility and functionality with smart home ecosystems and voice assistants, e.g. Alexa compatibility, Google Home integration, hub connectivity"
  },
  "Network Connectivity": {
    "definition": "Performance aspects related to wireless network connection stability and requirements, e.g. WiFi connection reliability, 2.4GHz requirements, connection drops"
  },
  "Installation Process": {
    "definition": "Performance aspects related to the physical installation and initial setup experience, e.g. ease of wiring, installation time, instruction clarity"
  },
  "Device Pairing": {
    "definition": "Performance aspects related to connecting devices to smart home systems and apps during initial setup, e.g. pairing success rate, setup mode functionality"
  },
  "Operational Reliability": {
    "definition": "Performance aspects related to consistent day-to-day functioning and dependability of core operations, e.g. reliable switching, consistent performance, operational stability"
  },
  "Product Longevity": {
    "definition": "Performance aspects related to how long devices continue to function before failure or breakdown, e.g. lifespan duration, failure timing, long-term durability"
  },
  "Physical Switch Quality": {
    "definition": "Performance aspects related to the tactile feel and mechanical operation of physical switch components, e.g. button action quality, rocker switch feel, switching force"
  },
  "Response Performance": {
    "definition": "Performance aspects related to speed and reliability of device response to commands, e.g. command execution delay, response consistency"
  },
  "Dimming Performance": {
    "definition": "Performance aspects related to light dimming functionality and quality, e.g. smooth dimming operation, dimming range, flickering issues"
  },
  "LED Compatibility": {
    "definition": "Performance aspects related to proper functioning with LED lighting systems, e.g. LED bulb compatibility, lighting performance with LEDs"
  },
  "Signal Range": {
    "definition": "Performance aspects related to wireless communication distance and signal strength, e.g. connection range limitations, signal reliability at distance"
  },
  "Multi-Way Switching": {
    "definition": "Performance aspects related to 3-way switch functionality and multi-switch configurations, e.g. 3-way operation, multiple switch recognition"
  },
  "Power Management": {
    "definition": "Performance aspects related to power handling and electrical performance, e.g. power outage recovery, voltage sensitivity, current handling"
  },
  "Indicator Light Performance": {
    "definition": "Performance aspects related to built-in indicator or pilot light functionality, e.g. indicator brightness, pilot light operation"
  }
}
