{
  "Basic Switching Operation": {
    "definition": "Core on/off switching functionality and responsiveness, e.g. turns lights on and off as expected, slow to respond, does not turn on/off without precise pressing"
  },
  "Dimming Control": {
    "definition": "Light brightness adjustment capabilities and dimming range performance, e.g. dims lights properly, brightness control works well, wish it could go dimmer more than 5%"
  },
  "Touch Interface Responsiveness": {
    "definition": "Touch sensitivity and tactile control experience for user interaction, e.g. touch sensitivity responsive, smooth continuous touch dimming, touch functionality spotty"
  },
  "Product Longevity": {
    "definition": "Long-term durability and lifespan under normal operating conditions, e.g. burned out after three months, holding up fairly well, didn't last a year"
  },
  "Installation Complexity": {
    "definition": "Ease of physical installation and setup process, e.g. simple to install in uncrowded box, connecting 3-way toggle was tricky, takes a lot of work"
  },
  "Smart Device Programming": {
    "definition": "Initial setup and configuration of smart features and connectivity, e.g. programming is fairly simple, could not be programmed without internet access, pairing was challenging"
  },
  "Smart Home Integration": {
    "definition": "Compatibility with smart home platforms and voice assistants, e.g. works great with Google Home and Alexa, does not work with SmartThings, no compatibility with Apple Home"
  },
  "Mobile App Experience": {
    "definition": "Quality and functionality of companion mobile applications, e.g. app is terrible, app doesn't allow timer settings, app worked half the time"
  },
  "LED Light Compatibility": {
    "definition": "Performance specifically with LED lighting systems and fixtures, e.g. eliminates LED flicker, works well with various LED fixtures, will not work well with non-dimmable bulbs"
  },
  "Electrical Reliability": {
    "definition": "Consistent electrical performance and freedom from malfunctions, e.g. multiple units failing within 2-4 months, switch shorted open, sparked when turned on"
  },
  "Circuit Configuration Support": {
    "definition": "Ability to work in different wiring configurations and setups, e.g. works single pole and 3-way, 3-way control didn't work, works in standard or 3-way configuration"
  },
  "Operational Noise Levels": {
    "definition": "Sound generation during normal operation, e.g. makes high pitch buzzing noise, quiet operating, doesn't cure buzzing with LED lights"
  },
  "Build Quality": {
    "definition": "Overall construction quality and material durability, e.g. higher quality than expected, cheap and fragile, heavy duty"
  },
  "Heat Management": {
    "definition": "Temperature control and thermal performance under load, e.g. handles 150 watt load great barely getting warm, won't get warm"
  },
  "Night Visibility": {
    "definition": "Illumination features for locating and using switches in dark conditions, e.g. night light allows switch to be seen in dark, light helps find wall switch at night, light not bright enough to see way into room"
  }
}
