data.response:
{
  "Installation Ease": {
    "definition": "Customer feedback about the simplicity and convenience of installing the switch, including time required, tools needed, and physical fit considerations, e.g. plug and play installation, easy to install in minutes, no tools required, hard to fit in junction box"
  },
  "Dimming Performance": {
    "definition": "Customer experiences with the dimming functionality including smoothness, range, brightness control, and fade effects, e.g. smooth dimming without jumps, dims down about 80%, soft ramp to on and off, limited dimming range"
  },
  "Device Compatibility": {
    "definition": "How well the switch works with different types of bulbs, fixtures, and electrical configurations, e.g. works with dimmable LED lights, compatible with 3-way setups, works with incandescent bulbs, causes LED flickering"
  },
  "Operational Reliability": {
    "definition": "Consistency and dependability of the switch's basic on/off and control functions over time, e.g. works great every time, turn on randomly, delayed response, switches not responding"
  },
  "Touch Interface Responsiveness": {
    "definition": "Customer feedback specifically about touch-based control sensitivity and ease of use, e.g. very responsive to touch, takes practice to get used to, not as easy to finely control as slider"
  },
  "Smart Connectivity": {
    "definition": "Performance of wireless features, app integration, and smart home system compatibility, e.g. wifi integration is flawless, responsive with Alexa, hard to get it to connect, app responds quickly"
  },
  "Long Term Durability": {
    "definition": "Product longevity, build quality, and resistance to wear over extended use periods, e.g. should last a long time, no plastic parts to break, gave out after 8 years, multiple units fail"
  },
  "Timer Functionality": {
    "definition": "Performance of built-in timer features for controlling connected devices like fans or lights, e.g. bottom switch is timer, fan timer works great, runs fan with timer button"
  },
  "Electrical Performance": {
    "definition": "Technical electrical operation including noise, buzzing, hesitation, and power delivery characteristics, e.g. no hum, buzz when switched on, hesitates when turning on, performs well"
  },
  "Memory and Recovery Features": {
    "definition": "How well the switch remembers settings and recovers from power interruptions, e.g. won't remember last dimmer setting, normally OFF after power cycle, hub takes minutes to come back online"
  },
  "Remote Control Performance": {
    "definition": "Effectiveness and reliability of wireless remote control functionality, e.g. pico remote working 35-40% of the time, remote is useless, repeaters have limited range"
  },
  "Software Experience": {
    "definition": "User experience with setup software, apps, and installation guidance, e.g. terrible software experience, showing countless errors, worst install experience"
  },
  "OUT_OF_SCOPE": {
    "definition": "Aspects that are too vague, generic, or unclear to categorize meaningfully",
    "ids": [894]
  }
}
